'use strict'
import 'antd/dist/antd.css'
import '@shopify/polaris/styles.css'
import dynamic from 'next/dynamic'
const AppProviderDynamic = dynamic(() =>
  Promise.resolve(require('@shopify/polaris').AppProvider)
)
import { Page, Select, TextField, Card } from '@shopify/polaris'

const DynamicAceEditor = dynamic(() => import('../components/aceEditor'), {
  ssr: false,
})

import { Row, Col } from 'antd'

const options = [
  { label: 'POST', value: 'post' },
  { label: 'GET', value: 'get' },
  { label: 'PUT', value: 'put' },
]

export default class Mocker extends React.Component {
  state = {
    body: '{}',
    endpoint: '',
    method: 'get',
    status: 200,
    output: `
  {
   endpoint:  http://localhost:5000,
   method: get,
   statusCode: 200,
    body: {}
  }
  `,
  }

  handleChange = async (newValue) => {
    await this.setState({ method: newValue })
    this.changeRender()
  }

  changeRender = () => {
    const output = `
  {
    endpoint:  http://localhost:5000/${this.state.endpoint},
    method: ${this.state.method},
    statusCode:  > ${this.state.status} ,
     body: ${this.state.body}
   }
  `

    this.setState({ output })
  }

  render() {
    return (
      <AppProviderDynamic>
        <Page fullWidth>
          <Row>
            <Col lg={12}>
              <Card
                sectioned
                title="Add new mock endpoint"
                primaryFooterAction={{ content: 'Add tracking number' }}
              >
                <TextField
                  label="Endpoint"
                  type="text"
                  value={this.state.endpoint}
                  prefix="http://localhost:5000/"
                  onChange={async text => {
                    const hText = text
                      .split(' ')
                      .join('-')
                      .toLowerCase()
                    await this.setState({ endpoint: hText })
                    this.changeRender()
                  }}
                />
                <p style={{ marginTop: 20 }} />
                <Select
                  label="Date range"
                  options={options}
                  onChange={this.handleChange}
                  value={this.state.method}
                />
                <p style={{ marginTop: 20 }} />
                <TextField
                  label="StatusCode"
                  type="number"
                  value={this.state.status}
                  onChange={async text => {
                    await this.setState({ status: text })
                    this.changeRender()
                  }}
                />
                <p style={{ marginTop: 20 }} />
                Request body
                <DynamicAceEditor
                  onChange={newValue => console.log(newValue)}
                  value={this.state.body}
                  lan="javascript"
                  theme="github"
                />
              </Card>
            </Col>

            <Col lg={11} offset={1}>
              <Card sectioned title="Render output">
                <DynamicAceEditor
                  lan="markdown"
                  height="70vh"
                  disabled={true}
                  value={this.state.output}
                  theme="github"
                />
              </Card>
            </Col>
          </Row>
        </Page>
      </AppProviderDynamic>
    )
  }
}
