'use strict'
import 'antd/dist/antd.css'; 
import '@shopify/polaris/styles.css'
import dynamic from 'next/dynamic'
const AppProviderDynamic = dynamic(() =>
  Promise.resolve(require('@shopify/polaris').AppProvider)
)
import { Page, Select,TextField, Card  } from "@shopify/polaris";

const DynamicAceEditor = dynamic(()=> import("../components/aceEditor"), { ssr: false })


import {  Row, Col } from "antd";


const options = [
  {label: 'POST', value: 'post'},
  {label: 'GET', value: 'get'},
  {label: 'PUT', value: 'put'},
];

export default class Mocker extends React.Component{
state ={
  body: "{}",
  endpoint: "",
  method: "get",
  status: 200,
  output: `
  {
   endpoint:  http://localhost:5000,
   method: get,
   statusCode: 200,
    body: {}
  }
  `,
}

handleChange = (newValue) => {
  console.log(newValue)
  this.setState({method: newValue});
};


    render () {
        return (
       <AppProviderDynamic>
<Page fullWidth>


<Row>

          <Col lg={12}>

          <Card sectioned title="Add new mock endpoint"
            primaryFooterAction={{content: 'Add tracking number'}}
          >
          <TextField
        label="Endpoint"
        type="text"
        prefix={`window.location.href`}
      />
      <p style={{marginTop: 20}}></p>
       <Select
        label="Date range"
        options={options}
        onChange={this.handleChange}
        value={this.state.method}
      />
            <p style={{marginTop: 20}}></p>
            <TextField
        label="StatusCode"
        type="number"
        value={this.state.status}
        onChange={(text)=> this.setState({ status: text })}
      />
                  <p style={{marginTop: 20}}></p>
                  Request body
            <DynamicAceEditor onChange={console.log} value={this.state.body} lan='javascript' theme="github"/>

          </Card>

          </Col>

          <Col lg={11} offset={1}>
<Card sectioned title="Render output">
<DynamicAceEditor lan='markdown' disabled={true} value={this.state.output} theme="github"/>
</Card>

</Col>
         </Row>


</Page>

       </AppProviderDynamic> 
        )
    }
}
