'use strict'
import 'antd/dist/antd.css'; 
import '@shopify/polaris/styles.css'
import dynamic from 'next/dynamic'
const AppProviderDynamic = dynamic(() =>
  Promise.resolve(require('@shopify/polaris').AppProvider)
)
import { Page, Form, Select,TextField, Card  } from "@shopify/polaris";

const DynamicAceEditor = dynamic(()=> import("react-ace"), { ssr: false })

import {  Row, Col } from "antd";


const options = [
  {label: 'POST', value: 'post'},
  {label: 'GET', value: 'get'},
  {label: 'PUT', value: 'put'},
];

export default class Mocker extends React.Component{
state ={
  output: "",
  body: "",
  endpoint: "",
  method: "get"
}

handleChange = (newValue) => {
  console.log(newValue)
  this.setState({method: newValue});
};


    render () {
        return (
       <AppProviderDynamic>
<Page fullWidth>


<Row>

          <Col lg={12}>

          <Card sectioned title="Add new mock endpoint">
          <TextField
        label="Endpoint"
        type="text"
        prefix={`window.location.href`}
      />
      <p style={{marginTop: 20}}></p>
   <DynamicAceEditor
    mode="javascript"
    theme="github"
    value={this.state.body}
    onChange={(data)=> this.setState({ body:  data })}
    name="UNIQUE_ID_OF_DIV"
    style={{height: 200}}
    editorProps={{$blockScrolling: true}}
    placeholder="Enter response body"
  />
       <Select
        label="Date range"
        options={options}
        onChange={this.handleChange}
        value={this.state.method}
      />
          </Card>

          </Col>

          <Col lg={11} offset={1}>

<Card sectioned>
<TextField
label="Endpoint"
type="number"
prefix="https://demoaccount.com/"
/>
<TextField
label="Price"
type="number"
prefix="$"
/>
</Card>

</Col>
         </Row>


</Page>

       </AppProviderDynamic> 
        )
    }
}
